    setSearch(e.target.value)
  }

  // const handleSubmit = e => {
  //   e.preventDefault();

  // }

  const handleSubmit = e => {
    e.preventDefault();
    this.props.onSearch(this.query.value);
    e.currentTarget.reset();
  }


  return ( 
    <form className="search-form" onSubmit={() => getSearch}>
