import { useState, useEffect } from "react";

const useFetch = (url) => {
  const [data, setData] = useState(null);
  const [isPending, setIsPending] = useState(true);
  const [error, setError] = useState(null);
  const [areResults, setAreResults] = useState(true)

  useEffect(() => {
    fetch(url)
      .then(res => {
        if (!res.ok){
          throw Error('Error: could not fetch the data for that resource')
        }
          return res.json();
      })
      .then(data => {
        if (data) {
          setIsPending(false);
          setData(data.photos.photo);
          setError(null);
        } else {
          setAreResults(false);
        }

      })
      .catch(err => {
        setIsPending(false);
        setError('Error: ' + err.message);
      })
  }, [url]);
  return { data, isPending, error, areResults }

}

export default useFetch;
