import Image from './Image'
import useFetch from './useFetch';
import apiKey from '../config';
import { useParams } from 'react-router-dom'

let api = apiKey;


const ImageList = () => {

  const {id} = useParams()

  const { data: images, isPending, setIsPending, error } = useFetch(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${api}&tags=${id}&per_page=24&format=json&nojsoncallback=1`)

  let results = false;
  // if (!isPending && images) {
  //   results = true;

  // } else if (!isPending) {
  //   results = false;
  // }


  let content;

  if (isPending) {
    content = <div>Loading...</div>
    results = true;
  } else if (!images) {
    results = false;
  } else if (error) {
    content =
    <div>{ error }</div>
  } else if (images) {
    content=
    <ul>
      { images && images.map((image) => (
          <Image  
            url={`https://farm${image.farm}.staticflickr.com/${image.server}/${image.id}_${image.secret}.jpg`}
            key={image.id}
          />
      ))}
      </ul>
  } else if (!images.length) {
    results = false;
  } else if (images.length) {
    results = false;
  }
  console.log(images)
  console.log(results)
  return (

    <div className="photo-container">
      { content }
      {/* <h2>Results</h2>
      { error && <div>{ error }</div> }
      { isPending && <div>Loading...</div> }
      <ul>
      { images && images.map((image) => (
          <Image  
            url={`https://farm${image.farm}.staticflickr.com/${image.server}/${image.id}_${image.secret}.jpg`}
            key={image.id}
          />
      ))}
      </ul> */}
      { !results && <div>Oops! There are no results for that search.</div> }
    </div>
   );
}

export default ImageList;
